import iconLogo from './images/Group 19@3x.png';
import textLogo from './images/logo@3x.png';
import './index.css';
import { useState } from 'react';

function Button({ active=false, children }) {
  return (
    <button
      className="button menu-button"
    >
      {children}
    </button>
  );
}

function NavMenuButtons() {  
  const arr = { menu1, menu2, menu3, menu4 };

  const [active, setActive] = useState(false);

  const handleNavMenu = (e) => {
    setActive(!active);
    console.log(e.active);
  }

  return (
    <div className="menu-container">
      <div>
        <Button
          className={active}
          onClick={handleNavMenu}
        >
          {arr[0]}
        </Button>
      </div>
      <div>
        <Button>
          {arr[2]}
        </Button>
      </div>
    </div>
  );
}

function NavBar() {
  return (
    <header>
      <nav>

        <div>
          <a href="/">
            <img className="icon-logo" src={iconLogo} alt='Logo' />
            <img className="text-logo invisible" src={textLogo} alt="text-logo" />
          </a>
        </div>
        <NavMenuButtons></NavMenuButtons>
        <div className="button-box">
          <button className="button login-button">로그인</button>
        </div>

      </nav>
    </header>
  );
}

export default NavBar
