import NavBar from "../layout/header";
import Content from "../layout/content";
import React, { useState, useEffect } from "react";

async function GetItemsInfo() {
  const response = await fetch("https://panda-market-api.vercel.app/products");
  const result = await response.json();
  return result;
}

function Item({ item }) {
  return (
    <div>
      <img className="content-img" src={item.image} alt={item.name} />
      <div>
        <h2>{item.name}</h2>
        <p>{item.name}</p>
        <p>{item.price}원</p>
        <p>{item.favoriteCount}</p>
      </div>
    </div>
  );
}

function ItemList({ items }) {
  return (
    <ul className="itmes">
      {items.map((item) => (
        <li key={item.id}>
          <Item item={item} />
        </li>
      ))}
    </ul>
  );
}

function App() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const data = await GetItemsInfo();
        setItems(data);
      } catch (error) {
        console.error("상품 정보를 가져오는 중 오류가 발생했습니다:", error);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      <NavBar />
      <Content />
      <ItemList GetItemsInfo={GetItemsInfo} />
    </div>
  );
}

export default App;
