import NavBar from "../layout/header";
import Content from "../layout/content";
import ItemList from "./items";
import GetItemsInfo from "./fetchItems";
import React, { useState, useEffect } from "react";

function App() {
  const [products, setProducts] = useState([]);
  const apiUrl = "https://panda-market-api.vercel.app/products";

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await fetch(apiUrl);
        const data = await response.json();

        // 데이터가 배열 형태로 오지 않는다면, 적절히 가공하여 배열로 변환
        const productList = Array.isArray(data) ? data : [data];

        setProducts(productList);
      } catch (error) {
        console.error("상품 정보를 가져오는 중 오류가 발생했습니다:", error);
      }
    }

    fetchProducts();
  }, []);

  return (
    <div>
      <NavBar />
      <Content />
      <ItemList />
    </div>
  );
}

export default App;
